mapper.insert:

insert into trail (id, uid, beginTime, 
  endTime, beginPosition, endPosition, 
  TOTAL_TIME, TOTAL_DISTANCE, AVG_SPEED
  )
values (#{id,jdbcType=INTEGER}, #{uid,jdbcType=INTEGER}, #{begintime,jdbcType=TIMESTAMP}, 
  #{endtime,jdbcType=TIMESTAMP}, #{beginposition,jdbcType=VARCHAR}, #{endposition,jdbcType=VARCHAR}, 
  #{totalTime,jdbcType=VARCHAR}, #{totalDistance,jdbcType=VARCHAR}, #{avgSpeed,jdbcType=VARCHAR}
  )

mapper.select:
select * from trail 
where 1=1

		and uid = #{uid}


		and id = #{id}

	order by beginTime desc
